import React, { Component } from 'react';


class SignUpForm extends Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: ''
    }
  }

  updateName = (e) => {
    this.setState({ name: e.target.value }, () => console.log(this.state.name))
  }

  updateEmail = (e) => {
    this.setState({ email: e.target.value }, () => console.log(this.state.email))
  }

  signUp = (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/signup', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        name: this.state.name,
        email: this.state.email
      })
    })
  }

  render() {
    return (
      <div className="container">
        <form>
        <div className="header-title"><h1>Sign up for the raffle!</h1></div>
        <div className="form-group">
          <input className="form-control" type="text" value={this.state.name} onChange={(this.updateName)} placeholder="Your name"/>
          <input className="form-control" type="email" value={this.state.email} onChange={this.updateEmail} placeholder="Your e-mail address"/>
        </div>
        <button className="btn btn-primary" onClick={this.signUp} type="submit">Sign Up</button>
        </form>
      </div>

    )
  }
}

export default SignUpForm;
