import React from 'react';
import './wheel.css';



const style = (degrees, color) => ({
	transform: `rotate(${degrees}deg)`,
	'-webkit-transform': `rotate(${degrees}deg)`,
	'-moz-transform': `rotate(${degrees}deg)`,
	'-o-transform': `rotate(${degrees}deg)`,
	'-ms-transform': `rotate(${degrees}deg)`,
	'border-color': `${color} transparent`,	
})

function renderWheel(participants) {
  return participants.map((participant, index, allParticipants) => {

  let color = '#'+(Math.random()*0xFFFFFF<<0).toString(16);
    const degree = index / allParticipants.length * 360
    return (
      <div className="sec" style={style(degree, color)}><span className="name">{participant.name}</span></div>
    )
  })
}

const spinStyle = (winnerNumber, participants) => {
  let degree;
  if (winnerNumber >= 0) {
   degree = (winnerNumber / participants.length * 360) + 360 * 6

  } else {
   degree = 0
  }
  return {
  transform: `rotate(${degree}deg)`
}}

const Wheel = ({participants, winnerNumber}) => {
  return (
    <div id="wrapper">

            <div id="wheel">
                <div style={spinStyle(winnerNumber, participants)} id="inner-wheel">
                    {renderWheel(participants)}
                </div>       

                <div id="spin">
                    <div id="inner-spin"><img src="https://hackages.io/static/media/logo_h_cube.6d1c5012.svg"/></div>
                </div>

                <div id="shine"></div>
            </div>

            <div id="txt"></div>
      </div>
  )
}

export default Wheel;


