import React, { Component } from 'react';
import './App.css';
import WheelOfFortune from './components/wheeloffortune';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import SignUpForm from './components/signup';

const url = 'http://localhost:3001';

export const AppRouter = () => {
  return (
    <Switch>
      <Route exact path='/' component={App}></Route>
      <Route exact path='/signup' component={SignUpForm}></Route>
    </Switch>
  )
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      participants: [],
      winner: '',
    }
  }

  componentDidMount() {
    fetch(`${url}/participants`)
      .then(res => res.json())
      .then(res => this.setState({ participants: res }))
  }

  generateWinner = () => {
    const { participants } = this.state;
    let randomNumber = Math.floor(Math.random() * participants.length);
    let winner = participants[randomNumber];
    this.setState({ winner: JSON.stringify(winner)})
  }

  render() {
    return(
      <Router>
        <div>Winner: {this.state.winner} </div>
        <WheelOfFortune generateWinner={this.generateWinner} />
        <Link to="/signup">Enter the Raffle</Link>
      </Router>
    )
  }
}

export default App;
