import React, { Component } from 'react';
import './App.css';
import WheelOfFortune from './components/wheeloffortune';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      participants: [ {'Sue': 'sue@gmail.com'}, {'Florian': 'florian@gmail.com'}, {'Davy': 'davy@hackages.io'}, {'Marta': 'marta@hackages.io'}],
      winner: '',
    }
  }

  componentDidMount() {
    // fetch participants
    // this.setState({ participants: data })
  }

  generateWinner = () => {
    e.preventDefault();
    const { participants } = this.state;
    let randomNumber = Math.floor(Math.random() * participants.length);
    let winner = participants[randomNumber];
    this.setState({ winner: winner})

  }

  render() {
    return(
      <div>
        <div>Winner: {this.state.winner} </div>
        <WheelOfFortune generateWinner={this.generateWinner} />
      </div>

    )
  }
}

export default App;
