import React, { Component } from 'react';
import './App.css';
import WheelOfFortune from './components/wheeloffortune';
import { Link } from 'react-router-dom';

const url = 'http://localhost:3001';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      participants: [],
      winner: '',
    }
  }

  componentDidMount() {
    fetch(`${url}/participants`)
      .then(res => res.json())
      .then(res => this.setState({ participants: res }))
  }

  generateWinner = () => {
    const { participants } = this.state;
    let randomNumber = Math.floor(Math.random() * participants.length);
    let winner = participants[randomNumber];
    this.setState({ winner: JSON.stringify(winner)})
  }

  render() {
    return(
      <div>
        <div>Winner: {this.state.winner} </div>
        <WheelOfFortune generateWinner={this.generateWinner} />
        <Link to="/signup">Enter the Raffle</Link>
      </div>
    )
  }
}

export default App;
